actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			id:selectSite.selectedOptionValue
		},
    query: `
query($id:ID!){
	getEquipment(id:$id){
		id
		name
		children(filter:{equipmentLevel:{eq:Warehouse},not:{isActive:false}}){
			value:id
			label:name
		}
	}
}
    `
})
}}

