{{
JSON.stringify({
    variables: {
			"input":{
				"warehouse":[{"name":selectWarehouse.selectedOptionLabel}],
				"material":[{"code":appsmith.store.materialCode}],
				"groupBy":{
      "material":true,
      "carrier":true,
      "status":true,
      "storageLocation":true
    }
			}
		},
    query: `
query ($input:StockOnHandFilter){
  getStockOnHand(filter:$input){
    material{
      code
    }
    carrier{
      id
				code
    }
    status{
      code
    }
    storageLocation{
      name
    }
    quantity
  }
}
    `
})
}}

