{{JSON.stringify({
	variables:{
		"input":appsmith.store.SelectedShipmentID
	},
	query:`
query getShipmentDetail($input:ID!){
  getShipment(id:$input){
			id
    consignmentNumber
    packedItems(filter:{isTerminal:true}){
			Qty
        FromPlace{
          storageLocation{
            name
          }
          carrier{
            code
          }
          materialSubLot{
            code
          }
        }
        material{
          code
        }
      }
  }
}
	`
})}}

